                        const glyphs = reducedGlyphSet != undefined ? reducedGlyphSet : Object.keys(font.glyphs.glyphs).map(a => String.fromCharCode(font.glyphs.glyphs[a].unicode));
                        const paths = glyphs.map( a => font.getPath(a,0,0,1) );
                    
