                        if(array_a.length == 0 && array_b.length == 0){
                            return {a:[],b:[]};
                        }
                        if(array_a.length == 0){
                            return {a:[],b:array_b};
                        }
                        if(array_b.length == 0){
                            return {a:array_a,b:[]};
                        }
                    
                        function arrayRemovals(a,b){
                            a.forEach(item => {
                                let i = b.indexOf(item);
                                if(i != -1){ b.splice(i,1); }
                            });
                            return b;
                        }
                    
                        return {
                            a:arrayRemovals(array_b,array_a.slice()),
                            b:arrayRemovals(array_a,array_b.slice())
                        };
